const express = require('express')
const app = express()
const PORT = 3000

app.use(express.urlencoded({ extended: true }))
app.use(express.json())
app.use((req, res, next) => {
    console.log(req.url, res.statusCode)
    next()
})
const characters = [
    {
        name: 'Yoda',
        role: 'Jedi Master',
        forcePoints: 10000,
        age: 900,
        avatar: 'data:image/jpeg;base64,',
        routeName: 'yoda'
    },
    {
        name: 'Luke Skywalker',
        role: 'Jedi Knight',
        forcePoints: 5000,
        age: 40,
        avatar: 'data:image/jpeg;base64,',
        routeName: 'lukeskywalker'
    },
    {
        name: 'Princess Leia',
        role: 'General',
        forcePoints: 1000,
        age: 40,
        avatar: 'data:image/jpeg;base64,',
        routeName: 'princessleia'
    }
]

app.get('/', (req, res) => {
    res.send('May the Force be with you')
})

// HTML ROUTES

// API ROUTES

// /api/characters - show all character data
app.get('/api/characters', (req, res) => {
    res.json(characters)
})

// /api/characters/:routeName - show individual character
app.get('/api/characters/:routeName', (req, res) => {
    const targetCharacter = req.params.routeName
    const character = characters.find(character => {
        return character.routeName === targetCharacter
    })
    res.json(character)
})

// add new characters
app.post('/api/characters/add', (req, res) => {
    // console.log(req.body)
    const newCharacter = req.body
    newCharacter.routeName = newCharacter.name.replace(/ /g, '').toLowerCase()
    
    characters.push(req.body)
    
    res.status(200).send()
})

app.listen(PORT, () => {
    console.log(`Server listening on http://localhost:${PORT}`)
})



